import React, {Component} from 'react'
import '../css/Table.css'


const USER_SERVICE_URL = 'StartApi.ashx?Platform=Android&ProcessType=';



class ArticelsTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isFetching: false,
            articels: [],
            orders: [],
            isShow: true ,
            ismodalvisible:false

        };
        this.GetOrders = this.GetOrders.bind(this)
    }

    Closeproductmodal(){
        console.log("sadasd")

        this.setState({ ismodalvisible:false});

    }


     async GetOrderEdit(OrderId,Dimensions,Color,Piece,ProductTypeName) {
        this.setState({ ismodalvisible:true});




        const response = await fetch(USER_SERVICE_URL+"Orders&ArticelId="+OrderId, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',    
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin":USER_SERVICE_URL+"Orders&ArticelId="+OrderId,
                "Authorization": "bearer "
            },
        });
        this.setState({ orders: await response.json(),isShow:false});

    };


       async GetOrders(ArticelId,ArticelName) {
        this.setState({ isShow:true});

        document.getElementById("ArticelName").innerHTML=ArticelName;
        document.getElementById("SecondScreen").classList.add("col-md-6");
        document.getElementById("FirstScreen").classList.add("col-md-6");
        document.getElementById("FirstScreen").classList.remove("col-md-12");


        const response = await fetch(USER_SERVICE_URL+"Orders&ArticelId="+ArticelId, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',    
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin":USER_SERVICE_URL+"Orders&ArticelId="+ArticelId,
                "Authorization": "bearer "
            },
        });
        this.setState({ orders: await response.json(),isShow:false});

    };

    render() {
        return (
            <div className="padd0 col-md-12">
                <div  class={this.state.isShow ? 'show ProgressSpinnerFlat' : 'opaq0 ProgressSpinnerFlat'} role="progressbar"> 
                <div aria-hidden="true">•</div>
                 <div aria-hidden="true">•</div> 
                 <div aria-hidden="true">•</div> 
                 <div aria-hidden="true">•</div> 
                 <div aria-hidden="true">•</div> 
                 </div>
            <div id="FirstScreen" className="WizardArea padd0 col-md-12">
            <table className="table table-hover">
            <thead>
            <tr className="alert alert-success">
                        <td tabindex="0" className="tablesorter-header tablesorter-unsorted">Firma</td>
                        <td tabindex="0" className="tablesorter-header tablesorter-unsorted">Articel / Sipariş</td>
                         <td tabindex="0" className="tablesorter-header tablesorter-unsorted">#</td>
                    </tr>
            </thead>
            <tbody>
                {this.state.articels.map(articels =>
                    <tr key={articels.id}   >
                        <td>{articels.CustomerName}</td>
                        <td ><span className="ArticelId">AT-{articels.id}</span> {articels.ArticelName}</td>
                         <td><i onClick={() => this.GetOrders(articels.id,articels.ArticelName)} className="Icon css-43 SearchIcon "></i></td> </tr>)}
            </tbody>
        </table></div>
        <div id="SecondScreen" class="WizardArea pad0">
        <div id="ArticelName" class="ArticelNameHead SSOrder text-capitalize PartHead">
           </div>



             <div id="TestBaby" class="DetailOrders SSOrder  OrderDetailsComment">
   <table class="pointer OrderDetailTable table table-hover">
      <thead>
         <tr class="alert alert-success">
            <td tabindex="0" class="tablesorter-header tablesorter-unsorted">Adet</td>
            <td tabindex="0" class="tablesorter-header tablesorter-unsorted">Ölçü</td>
            <td tabindex="0" class="tablesorter-header tablesorter-unsorted">Renk</td>
            <td tabindex="0" class="tablesorter-header tablesorter-unsorted">Tip</td>
            <td tabindex="0" class="tablesorter-header tablesorter-unsorted">#</td>

         </tr>
      </thead>
      <tbody aria-live="polite">
      {this.state.orders.map(o =>
         <tr data-piece={o.Piece} data-product={o.Color} class="MotionData" key={o.id} data-orderid={o.id}>
            <td>
               <div class="ms-CalloutExample">
                  <div class="ms-Callout ms-Callout--arrowLeft ms-Callout--OOBE is-hidden">
                     <div class="ms-Callout-main">
                        <div class="ms-Callout-header">
                           <p class="ms-Callout-title"> </p>
                        </div>
                        <div class="ms-Callout-inner">
                           <div class="ms-Callout-content">
                              <p class="ms-Callout-subText"></p>
                           </div>
                        </div>
                     </div>
                  </div>
                  <div class="ms-CalloutExample-button">
                      <button data-piece={o.Piece} class="ms-Button info">{o.Piece} {o.Metrics}</button></div>
                  <br/> 
               </div>
            </td>
            <td>{o.Dimensions} </td>
            <td>{o.Color} </td>

            <td> {o.ProductTypeName} </td>

            <td><i  
             onClick={() => this.GetOrderEdit(o.id,o.Dimensions,o.Color,o.Piece,o.ProductTypeName)}
            title="Düzenle" data-icon-name="Edit" role="presentation" aria-hidden="true" class="ms-Button-icon fleft icon-73"></i></td>
         </tr>
         )}
      </tbody>
   </table>
</div> </div>

<div id="ProductModal" className={this.state.ismodalvisible ? 'ms-Layer ms-Layer--fixed layer-351' : 'hide ms-Layer ms-Layer--fixed layer-351'} data-portal-element="true">
        <div class="ms-Fabric ms-Layer-content content-120">
            <div role="dialog"  >
                <div class="ms-Modal is-open root-345">
                    <div class="ms-Overlay ms-Overlay--dark root-414"></div>
                    <div class="ms-Dialog-main HudldIlvnIFz-AVg_Bopj main-412">
                        <div tabindex="0" data-is-visible="true"  ></div>
                        <div class="ms-Modal-scrollableContent scrollableContent-413" data-is-scrollable="true">
                            <div className="fright">
                                <i data-icon-name="Cancel" onClick={() => this.Closeproductmodal()} role="presentation" aria-hidden="true" class="pointer ms-Button-icon icon-73"></i>
                            </div>

                            <div  class="ProductModalSub OneProductEdit">


                                 <div class="col-md-12 fleft">
                                        <div class="col-md-6 fleft">
                                            <label>Adet</label>
                                            <input type="text" class="OneOrderEditPiece  form-control OneDriveInput"/><br/>
                                        </div>
                                        <div class="col-md-6 fleft">
                                            <label>Ölçü</label>
                                            <input type="text" class="OneOrderEditDimensions  form-control OneDriveInput"/><br/>
                                        </div>
                                    </div>

                                    <div class="col-md-12 fleft">
                                        <div class="col-md-6 fleft">
                                            <label>Ürün Tipi</label>
                                            <select class="OneOrderEditType ProductType  form-control OneDriveInput">
                                                <option value="1">Tiktak</option> </select><br/>
                                        </div>
                                        <div class="col-md-6 fleft">
                                            <label>Renk</label>
                                            <input type="text" class="OneOrderEditColor  form-control OneDriveInput"/><br/>
                                        </div>
                                    </div> 
                                <div class="col-md-12 text-center fleft">
                                        <span id="OneOrderEditPiece" data-orderid="9243" class=" pad10 TransferBTN ms-Button ms-Button--primary">Kaydet</span><br/>
                                     </div>

                            </div>



                        </div>
                     </div>
                </div>
            </div>
        </div>
    </div>





        </div>
        )
    }

    componentDidMount() {
        this.fetcharticels();


    }

    componentWillUnmount() {
        clearInterval(this.timer);
        this.timer = null;
    }

    async fetcharticelsAsync() {

        const response = await fetch(USER_SERVICE_URL+"Articels", {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',    
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin":USER_SERVICE_URL+"Articels",
                "Authorization": "bearer "
            },
        });
        this.setState({ articels: await response.json(),isShow:false });


    };

    fetcharticels = this.fetcharticelsAsync;
}

export default ArticelsTable
