import React, {Component} from 'react'
 import '../css/Table.css'
import '../../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css'


const USER_SERVICE_URL = 'StartApi.ashx?ProcessType=Articels&Platform=Android';



class UserTableAutonomous extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isFetching: false,
            users: []
        };
    }

    render() {
        return (

            <table class="table table-hover">
            <thead>
            <tr class="alert alert-success">
                        <td tabindex="0" class="tablesorter-header tablesorter-unsorted">Firma</td>
                        <td tabindex="0" class="tablesorter-header tablesorter-unsorted">Articel / Sipari≈ü</td>
                        <td tabindex="0" class="tablesorter-header tablesorter-unsorted">Tarih</td>
                        <td tabindex="0" class="tablesorter-header tablesorter-unsorted">#</td>
                    </tr>
            </thead>
            <tbody id="listteklif">
                {this.state.users.map(users =>
                    <tr key={users.id} data-id={users.id}   class="talep detay ">
                        <td>{users.CustomerName}</td>
                        <td>{users.ArticelName}</td>
                        <td><i  class="Icon SearchIcon css-43"></i></td> </tr>)}
            </tbody>
        </table>

        )
    }

    componentDidMount() {
        this.fetchUsers();
        this.timer = setInterval(() => this.fetchUsers(), 5000);
    }

    componentWillUnmount() {
        clearInterval(this.timer);
        this.timer = null;
    }

    async fetchUsersAsync() {

        const response = await fetch(USER_SERVICE_URL, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',    
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin":USER_SERVICE_URL,
                "Authorization": "bearer "
            },
        });
        this.setState({ users: await response.json() });


    };

    fetchUsers = this.fetchUsersAsync;
}

export default UserTableAutonomous
