import React, {Component} from 'react'
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table'



function rowClassNameFormat(rowIdx) {
    return rowIdx % 2 === 0 ? 'Gold-Row' : 'Silver-Row';
}


class UserTableAutonomous extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isFetching: false,
            users: []
        };
    }

    render() {
        return (
            <div>
                <BootstrapTable data={this.state.users} trClassName={rowClassNameFormat}>
                    <TableHeaderColumn isKey dataField='id'></TableHeaderColumn>
                    <TableHeaderColumn dataField='ArticelName'></TableHeaderColumn>
                    <TableHeaderColumn dataField='CustomerName'></TableHeaderColumn>
                </BootstrapTable>
                <p>{this.state.isFetching ? 'Fetching users...' : ''}</p>
            </div>
        )
    }

    componentDidMount() {
        this.fetchUsers();

      const express = require('express');
      const request = require('request');

      const app = express();

      app.use((req, res, next) => {
        res.header('Access-Control-Allow-Origin', '*');
        next();
      });

      app.get('StartApi.ashx?ProcessType=Articels&Platform=Android', (req, res) => {
        request(
          { url: 'https://statu.space/' },
          (error, response, body) => { 
            res.json(JSON.parse(body));
          }
        )
      });


        this.timer = setInterval(() => this.fetchUsers(), 5000);
    }

    componentWillUnmount() {
        clearInterval(this.timer);
        this.timer = null;
    }

    async fetchUsersAsync() {




    };

    fetchUsers = this.fetchUsersAsync;
}

export default UserTableAutonomous
