import React, { Component } from "react";

import Cookies from "js-cookie";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      pass: "",
      result: "",
    };

    this.LoginProccess = this.LoginProccess.bind(this);
  }

  async LoginProccess() {
    const response = await fetch("https://api.statu.space/token", {
      method: "POST",
      cache: "no-cache",
      mode: "cors",
      body:
        "grant_type=password&username=" +
        this.state.name +
        "&password=" +
        this.state.pass,
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
        "access-control-allow-credentials": false,
        "Access-Control-Allow-Origin": "https://api.statu.space/token",
      },
    });
    this.setState({ result: await response.json() });
    setTimeout(() => this.chechlogin(), 1000);
  }
  chechlogin() {
    [this.state.result].map((postData) => {
      Cookies.set("Auth", postData.access_token);
      window.location.reload();
    });
  }
  ChangeName(value) {
    this.setState({ name: value });
  }
  ChangePass(value) {
    this.setState({ pass: value });
  }

  render() {
    return (
      <div
        style={{ width: "350px", margin: "0 auto" }}
        className="sign-in-box ext-sign-in-box fade-in-lightbox"
      >
        <div>
          <div id="Page" style={{ width: "350px", marginTop: "100px" }}>
            <b>E-posta</b> <br />
            <input
              type="email"
              className="form-control ltr_override input ext-input text-box ext-text-box"
              onChange={(e) => this.ChangeName(e.target.value)}
              defaultValue={this.state.name}
            />
            <br />
            <b>Şifre</b>
            <div style={{clear:"bot"}}></div>
            <input
              type="password"
              className="form-control ltr_override input ext-input text-box ext-text-box"
              onChange={(e) => this.ChangePass(e.target.value)}
              defaultValue={this.state.pass}
            />
            <span
              className="btn btn-success"
              onClick={() => this.LoginProccess()}
            >
              Giriş Yap
            </span>
          </div>
        </div>{" "}
      </div>
    );
  }

  componentDidMount() {}

  componentWillUnmount() {}
}

export default Login;
