import React, {Component} from 'react'
import '../css/Table.css'
import CallOut from './CallOut';
import Files from './Files';
import WayBillList from './WayBillList';
import TopBar from './TopBar';
import Modal from './Modal';
import LayoutRight from './LayoutRight';


const USER_SERVICE_URL = 'StartApi.ashx?Platform=Android&ProcessType=';
const FilesUrl = 'abi/post/OrderPictures.ashx?ArticelId=';

class ArticelsTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isFetching: false,
            topbarvisible:false,
            isLayoutRight:false,
            articels: [],
            orders: [],
            isShow: true,
            ismodalvisible: false,
            isproductoutshow:false,
            isShowOrder: false,
            iscalloutshow: false,
            waybill: [],
            onewaybill: [],
            Piece: 0,
            Dimensions: 0,
            Color: "",
            TypeName: "",
            TypeId: 0,
            loopcount: 0,
            totalOutPiece: 0,
            totalOutWeight: 0,      
            articelid:0,    
            iswaybilllist:true,
            isShowFiles:false,
            isfirstrun:true,
            files:[],
            x: 0,
            y: 0,articelname:""

        };

        this.GetOrders = this
            .GetOrders
            .bind(this)

            this.productOutShow = this.productOutShow.bind(this)
            this.CancelProduct = this.CancelProduct.bind(this)

            this.closeTopBar = this.closeTopBar.bind(this)

            this.LayoutRightShow = this.LayoutRightShow.bind(this)



    }

    Closeproductmodal() {

        this.setState({ismodalvisible: false,iscalloutshow: false});

    }
LayoutRightShow(){

    this.setState({isLayoutRight: true,topbarvisible:false,iscalloutshow: false});
    document.getElementById("LayoutRight").style.width = "300px";



}
    productOutShow() {


        this.setState({isproductoutshow: true,iscalloutshow: false});



    }
    CancelProduct(){
        this.setState({isproductoutshow: false,iscalloutshow: false});


    }


    CancelCallOut(){
        this.setState({iscalloutshow: false});


    }

    closeTopBar(){

        this.setState({topbarvisible: false,iscalloutshow: false});

        document.getElementById("SecondScreen").classList.add("hide");
        document.getElementById("FirstScreen").classList.add("col-md-12");
        document.getElementById("FirstScreen").classList.remove("col-md-4");

    }

    TransferSummary(weight, piece) {

        this.setState({
            totalOutPiece: 1 + piece,
            totalOutWeight: 1 + weight,
            loopcount: 99
        });

    }

    async GetWayBillPhoto(WayBillId) {}
    async GetWayBillAsync(ArticelId) {

        const response = await fetch(USER_SERVICE_URL + "Motion&MotionType=Multi&OrderId=" + ArticelId, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin": USER_SERVICE_URL + "Motion&MotionType=Multi&OrderId=" + ArticelId,
                "Authorization": "bearer "
            }
        });
        this.setState({
            waybill: await response.json(),isShow: false
        });
if(this.state.waybill==''){

    this.setState({iswaybilllist: false});
}else{
    this.setState({iswaybilllist: true});

}
    }

    async GetFilesAsync(ArticelId) {
var url= USER_SERVICE_URL + "Pictures&ArticelId="+ArticelId;
        const response = await fetch(url, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin": url ,
                "Authorization": "bearer "
            }
        });
        this.setState({
            files: await response.json(),isShowFiles: false
        });
if(this.state.files==''){

    this.setState({isShowFiles: false});
}else{
    this.setState({isShowFiles: true});

}
    }









    async GetOrderEdit(OrderId, Dimensions, Color, Piece, ProductTypeName) {

        this.setState({ismodalvisible: true, Piece: Piece, Dimensions: Dimensions, Color: Color, ProductTypeName: ProductTypeName});

        const response = await fetch(USER_SERVICE_URL + "Orders&ArticelId=" + OrderId, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin": USER_SERVICE_URL + "Orders&ArticelId=" + OrderId,
                "Authorization": "bearer "
            }
        });

    };












    _onMouseMove(e) {
        this.setState({
            x: e.pageX + "px",
            y: e.pageY - 100 + "px"
        });

    }
    async GetWaybillforOrder(OrderId, dimensions, color, producttypename, element) {

        this.setState({Dimensions: dimensions, Color: color, ProductTypeName: producttypename, iscalloutshow: true,isShow: true});

        const response = await fetch(USER_SERVICE_URL + "Motion&MotionType=One&OrderId=" + OrderId, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin": USER_SERVICE_URL + "Motion&MotionType=One&OrderId=" + OrderId,
                "Authorization": "bearer "
            }
        });
        this.setState({
            onewaybill: await response.json(),isShow: false
        });
        var wayPiece = 0;
        var wayWeight = 0;

        this.state.onewaybill.map((w) => wayPiece = wayPiece + parseInt(w.Piece));

        this.setState({loopcount: this.state.onewaybill.length, totalOutPiece: wayPiece, totalOutWeight: wayWeight});

    }

    async GetOrders(ArticelId, ArticelName) {



        this.setState({isShow: true,topbarvisible:true,  iscalloutshow: false,articelname:ArticelName});
        {this.GetWayBillAsync(ArticelId)}   
        {this.GetFilesAsync(ArticelId)}   



        document.getElementById("ArticelName").innerHTML = ArticelName;
        document.getElementById("SecondScreen").classList.remove("hide");

        document.getElementById("SecondScreen").classList.add("col-md-8");
        document.getElementById("FirstScreen").classList.add("col-md-4");
        document.getElementById("FirstScreen").classList.remove("col-md-12");

        const response = await fetch(USER_SERVICE_URL + "Orders&ArticelId=" + ArticelId, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin": USER_SERVICE_URL + "Orders&ArticelId=" + ArticelId,
                "Authorization": "bearer "
            }
        });
        this.setState({
            orders: await response.json(),
            isShow: false,
            isShowOrder: true
        });

    };

    render() {

        return (


            <div className="padd0 col-md-12">


<TopBar closeTopBar={this.closeTopBar} LayoutRightShow={this.LayoutRightShow} topbarvisible={this.state.topbarvisible} productOutShow={this.productOutShow}/>

<div  className={this.state.isfirstrun
                    ? 'show'
                    : 'opaq0 hide'}
                    style={{left:0,top:0,bottom:"1080px",right:0,position:'fixed',zIndex:99999999999999999,width:1920,height:1080,backgroundColor:"#fff",fontSize:"100px !important",paddingTop:"450px"}}

 >

<div className="text-center">İlk Açılış ayarlanıyor</div>


<div className="prf ProgressSpinnerFlat"

                    role="progressbar">
                    <div aria-hidden="true">•</div>
                    <div aria-hidden="true">•</div>
                    <div aria-hidden="true">•</div>
                    <div aria-hidden="true">•</div>


                </div>

                </div>


                <div
                    className={this.state.isShow
                    ? 'show ProgressSpinnerFlat'
                    : 'opaq0 ProgressSpinnerFlat'}
                    role="progressbar">
                    <div aria-hidden="true">•</div>
                    <div aria-hidden="true">•</div>
                    <div aria-hidden="true">•</div>
                    <div aria-hidden="true">•</div>
                    <div aria-hidden="true">•</div>
                    <div aria-hidden="true">•</div>
                    <div aria-hidden="true">•</div>

                </div>
                <div id="FirstScreen" className="WizardArea padd0 col-md-12">
                    <table className="table table-hover">
                        <thead>
                            <tr className="alert alert-success">
                                <td >Firma</td>
                                <td >Articel / Sipariş</td>
                                <td >#</td>
                            </tr>
                        </thead>
                        <tbody>
                        {this.state.articels.map(a => <tr key={a.id}>
                                    <td>{a.CustomerName}</td>
                                    <td >
                                        <span className="ArticelId">AT-{a.id}</span>
                                        {a.ArticelName}</td>
                                    <td>
                                        <i onClick={() => this.GetOrders(a.id, a.ArticelName)} className="Icon css-43 SearchIcon "></i>
                                    </td>
                                </tr>)}
                        </tbody>
                    </table>
                </div>
                <div id="SecondScreen" className={this.state.isShowOrder ? 'WizardArea padd0 col-md-8' : 'hide'}>

                <div id="ArticelName" className="ArticelNameHead SSOrder text-capitalize PartHead"></div>

                    <div className="DetailOrders SSOrder  OrderDetailsComment">                      

                        <table className="pointer OrderDetailTable table table-hover">
                            <thead>
                                <tr className="alert alert-success">
                                    <td >Adet</td>
                                    <td>Ölçü</td>
                                    <td >Renk</td>
                                    <td>Tip</td>
                                    <td >#</td>

                                </tr>
                            </thead>
                            <tbody aria-live="polite">
                                {this.state.orders.map(o => <tr data-piece={o.Piece} data-product={o.Color} className="MotionData" key={o.id} data-orderid={o.id}>
                                        <td>
                                        <div onClick={this._onMouseMove.bind(this)}>
                                         <span data-piece={o.Piece} onClick={() => this.GetWaybillforOrder(o.id, o.Dimensions, o.Color, o.ProductTypeName, this)}>{o.Piece} {o.Metrics}</span>
                                            </div>

                                        </td>
                                        <td>{o.Dimensions}
                                        </td>
                                        <td>{o.Color}
                                        </td>

                                        <td>
                                            {o.ProductTypeName}  
                                        </td>

                                        <td>  

                                            <i onClick={() => 
                                                    this.GetOrderEdit(o.id, 
                                                    o.Dimensions, 
                                                    o.Color, 
                                                    o.Piece, 
                                                    o.ProductTypeName)}
                                                title="Düzenle"
                                                data-icon-name="Edit"
                                                role="presentation"
                                                aria-hidden="true"
                                                className="ms-Button-icon fleft icon-73"></i>

                                        </td>
                                    </tr>)}
                            </tbody>
                        </table>
                    </div>

<div className={this.state.iswaybilllist ? '' : 'hide'}>
<div className="PartHead">
 İrsaliyeler 
</div>

<WayBillList waybill={this.state.waybill} />





</div>
<div className={this.state.isShowFiles ? '' : 'hide'}>
<div className="PartHead">
 Dökümanlar 
</div>

<Files files={this.state.files} />

<LayoutRight isLayoutRight={this.state.isLayoutRight} />






</div>

                </div>

                <div
                    className={this.state.iscalloutshow ? '' : 'hide'}>
                   <div id="PopupWayBill" style={{ top: this.state.y, left: this.state.x }}
                        className="ms-ContextualHost is-positioned ms-ContextualHost--arrowLeft is-open ms-ContextualHost--primaryArrow">
          <i data-icon-name="Cancel"  className="fright forcewhite ms-Button-icon icon-73"></i>

                        <CallOut   
                            CancelCallOut={this.CancelCallOut}
                            totalOutPiece={this.state.totalOutPiece}
                            loopcount={this.state.loopcount}
                            Dimensions={this.state.Dimensions}
                            Color={this.state.Color}
                            ProductTypeName={this.state.ProductTypeName}
                            onewaybill={this.state.onewaybill}/>
                    </div>
                </div>
                <div  className={this.state.isproductoutshow ? '' : 'hide'}>
                <Modal CancelProduct={this.CancelProduct} orderlist={this.state.orders} articelname={this.state.articelname} />

                </div>
            </div>
        )
    }

    updateDimensions = () => {
        this.setState({iscalloutshow: false});
    };

    componentDidMount() {
        this.fetcharticels();
        window.addEventListener('resize', this.updateDimensions);

    }

    componentWillUnmount() {
        clearInterval(this.timer);
        this.timer = null;
    }

    async fetcharticelsAsync() {

        const response = await fetch(USER_SERVICE_URL + "Articels", {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin": USER_SERVICE_URL + "Articels",
                "Authorization": "bearer "
            }
        });
        this.setState({
            articels: await response.json(),
            isShow: false,isfirstrun:false
        });

    };

    fetcharticels = this.fetcharticelsAsync;
}

export default ArticelsTable
