import React, {Component} from 'react'
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table'
import '../css/Table.css'
import '../../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css'
import axios from 'axios'

const USER_SERVICE_URL = 'https://statu.space/StartApi.ashx?ProcessType=Articels&Platform=Android';

function rowClassNameFormat(row, rowIdx) {
    return rowIdx % 2 === 0 ? 'Gold-Row' : 'Silver-Row';
}


class UserTableAutonomous extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isFetching: false,
            users: []
        };
    }

    render() {
        return (
            <div>
                <BootstrapTable data={this.state.users} trClassName={rowClassNameFormat}>
                    <TableHeaderColumn isKey dataField='id'></TableHeaderColumn>
                    <TableHeaderColumn dataField='ArticelName'></TableHeaderColumn>
                    <TableHeaderColumn dataField='CustomerName'></TableHeaderColumn>
                </BootstrapTable>
                <p>{this.state.isFetching ? 'Fetching users...' : ''}</p>
            </div>
        )
    }

    componentDidMount() {
        this.fetchUsers();
        this.timer = setInterval(() => this.fetchUsers(), 5000);
    }

    componentWillUnmount() {
        clearInterval(this.timer);
        this.timer = null;
    }

    async fetchUsersAsync() {

         var whitelist = ['https://statu.space']
         var corsOptions = {
           origin: function (origin, callback) {
             if (whitelist.indexOf(origin) !== -1) {
               callback(null, true)
             } else {
               callback(new Error('Not allowed by CORS'))
             }
           }
         }

         // Then pass them to cors:
         app.use(cors(corsOptions));




          axios.get(USER_SERVICE_URL)
           .then((res) => {
            this.setState({users: res.data, isFetching: false});
           })
           .catch((err) => {
             console.log("ERROR: ====", err);
           })




    };

    fetchUsersAsync;
}

export default UserTableAutonomous
