import React, { Component } from "react";
import "../css/Table.css";
import { useCookies } from "react-cookie";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      pass: "",
      result: "",
    };
    const [cookies, setCookie, removeCookie] = useCookies(["user"]);

    this.LoginProccess = this.LoginProccess.bind(this);
  }
  ChangeName(value) {
    this.setState({ name: value });
  }
  ChangePass(value) {
    this.setState({ pass: value });
  }

  render() {
    return (
      <div className="padd0 col-md-12">
        <input
          type="text"
          className="OneDriveInput form-control"
          onChange={(e) => this.ChangeName(e.target.value)}
          defaultValue={this.state.name}
        />
        <input
          type="text"
          className="OneDriveInput form-control"
          onChange={(e) => this.ChangePass(e.target.value)}
          defaultValue={this.state.pass}
        />

        <span className="btn btn-primary" onClick={() => this.LoginProccess()}>
          Giriş Yap
        </span>
      </div>
    );
  }

  componentDidMount() {}

  componentWillUnmount() {}

  async LoginProccess() {
    const response = await fetch("https://api.statu.space/token", {
      method: "POST",
      cache: "no-cache",
      mode: "cors",
      body:
        "grant_type=password&username=" +
        this.state.name +
        "&password=" +
        this.state.pass,
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
        "access-control-allow-credentials": false,
        "Access-Control-Allow-Origin": "https://api.statu.space/token",
      },
    });
    this.setState({ result: await response.json() });
    console.log(this.state.result);
    setTimeout(() => this.chechlogin(), 5000);
  }
  chechlogin() {
    [this.state.result].map((postData) => {
      setCookie
      useCookies.setRawCookie("userId", postData.access_token, { path: "/" });
    });
  }
}

export default Login;
