import React, { Component } from "react";
import "../css/Table.css";
import Cookies from "js-cookie";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      pass: "",
      result: "",
    };

    this.LoginProccess = this.LoginProccess.bind(this);
  }

  async LoginProccess() {
    const response = await fetch("https://api.statu.space/token", {
      method: "POST",
      cache: "no-cache",
      mode: "cors",
      body:
        "grant_type=password&username=" +
        this.state.name +
        "&password=" +
        this.state.pass,
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
        "access-control-allow-credentials": false,
        "Access-Control-Allow-Origin": "https://api.statu.space/token",
      },
    });
    this.setState({ result: await response.json() });
    setTimeout(() => this.chechlogin(), 5000);
  }
  chechlogin() {
    [this.state.result].map((postData) => {
      Cookies.set("Auth", postData.access_token);
    });
  }
  ChangeName(value) {
    this.setState({ name: value });
  }
  ChangePass(value) {
    this.setState({ pass: value });
  }

  render() {
    return <div className="container">
<Login/>
<MainPage/>

    </div>;
  }

  componentDidMount() {}

  componentWillUnmount() {}
}

export default App;
