import React, {Component} from 'react'
import '../css/Table.css'
import CallOut from './CallOut';

const USER_SERVICE_URL = 'StartApi.ashx?Platform=Android&ProcessType=';

class ArticelsTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isFetching: false,
            articels: [],
            orders: [],
            isShow: true,
            ismodalvisible: false,
            isShowOrder: false,
            iscalloutshow: false,
            waybill: [],
            onewaybill: [],
            Piece: 0,
            Dimensions: 0,
            Color: "",
            TypeName: "",
            TypeId: 0,
            loopcount: 0,
            totalOutPiece: 0,
            totalOutWeight: 0,
