import React, { Component } from "react";
import "../css/Table.css";

class ProductNewModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
     };

    this.SaveOrder = this.SaveOrder.bind(this);
  }

  SaveOrder() {
    this.PostOrderSave();
  }

  async PostOrderSave() {
    this.setState({ isproducteditshow: false });

    var url =
      "abi/post/AddOrder.ashx?" +
      "ArticelId=" +
      this.state.ArticelId +
      "&ProductType=" +
      this.state.TypeId +
      "&Dimensions=" +
      this.state.Dimensions +
      "&CorpId=" +
      this.state.CorpId +
      "&Color=" +
      this.state.Color +
      "&Piece=" +
      this.state.Piece;
    const response = await fetch(url, {
      method: "POST",
      cache: "no-cache",
      mode: "cors",
      headers: {
        "Content-Type": "application/json",
        "access-control-allow-credentials": false,
        "Access-Control-Allow-Origin": url,
        Authorization: "bearer ",
      },
    });
  }



  render() {
    return (
      <div className="padd0 col-md-12">
        <div
          id="ProductModal"
          className="ms-Layer ms-Layer--fixed layer-351"
          data-portal-element="true"
        >
          <div className="ms-Fabric ms-Layer-content content-120">
            <div role="dialog">
              <div className="ms-Modal is-open root-345">
                <div className="ms-Overlay ms-Overlay--dark root-414"></div>
                <div className="ms-Dialog-main HudldIlvnIFz-AVg_Bopj main-412">
                  <div className="ms-Modal-scrollableContent scrollableContent-413">
                    <div>
                      <i
                        data-icon-name="Cancel"
                        onClick={() => props.CancelNewProduct()}
                        role="presentation"
                        aria-hidden="true"
                        className="pointer ms-Button-icon icon-73"
                      >
                        
                      </i>
                    </div>
                    <div className="ProductModalSub ProductOut">
                      <div>
                        <h4>Yeni Ürün Ekle</h4>
                        <h4 className="ProductName padd0 text-left col-md-12"></h4>
                        <br />
                      </div>
                      <hr />
                      <div className="col-md-12 fleft">
                        <select
                          onChange={(e) =>
                            props.ChangeProductType(e.target.value)
                          }
                        >
                          {props.ProductTypes.map((p) => (
                            <option value={p.id}>{p.Name}</option>
                          ))}
                        </select>
                      </div>

                      <div className="col-md-12">
                        <div className="clearfix OrderRow">
                          <div className="col-md-2 fleft">
                            <span>Adet</span>
                          </div>
                          <div className="col-md-10 fleft">
                            <input
                              type="number"
                              value={props.Piece}
                              className="Piece form-control"
                            />
                          </div>
                          <div className="col-md-2 fleft">
                            <span>Ölçü</span>
                          </div>
                          <div className="col-md-10 fleft">
                            <input
                              type="text"
                              value={props.Dimensions}
                              className="Dim form-control"
                            />
                          </div>
                          <div className="col-md-2 fleft">
                            <span>Renk</span>
                          </div>
                          <div className="col-md-10 fleft">
                            <input
                              type="text"
                              value={props.Color}
                              className="Color form-control"
                            />
                          </div>

                          <div className="col-md-12 text-right fleft">
                            <span className="Transfer TransferBTN ms-Button ms-Button--primary">
                              <span
                                onClick={() => props.SaveOrder(props.Color)}
                                className="transfersavetext"
                              >
                                Kaydet
                              </span>
                            </span>
                          </div>
                        </div>
                        <hr />
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  updateDimensions = () => {
    this.setState({ IsCallOutShow: false });
  };

  componentDidMount() {
    this.fetcharticels();
    window.addEventListener("resize", this.updateDimensions);
  }
}

export default ProductNewModal;
