import React, { Component } from "react";
import "../css/Table.css";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      pass: "",
    };

    this.LoginProccess = this.LoginProccess.bind(this);
  }
  ChangeName(value) {
    this.setState({ name: value });
  }
  ChangePass(value) {
    this.setState({ pass: value });
  }

  render() {
    return (
      <div className="padd0 col-md-12">
        <input
          type="text"
          className="OneDriveInput form-control"
          onChange={(e) => this.ChangeName(e.target.value)}
          defaultValue={this.state.name}
        />
        <input
          type="text"
          className="OneDriveInput form-control"
          onChange={(e) => this.ChangePass(e.target.value)}
          defaultValue={this.state.pass}
        />

        <span className="btn btn-primary" onClick={() => this.LoginProccess()}>
          Giri≈ü Yap
        </span>
      </div>
    );
  }

  componentDidMount() {}

  componentWillUnmount() {}

  async LoginProccess() {
    let formData = new FormData();

    formData.append("username", this.state.name);
    formData.append("password", this.state.pass);
    formData.append("grant_type", "password");

    const response = await fetch("https://api.statu.space/token", {
      method: "POST",
      cache: "no-cache",
      mode: "cors",
      body: {
        username: this.state.name,
        password: this.state.pass,
        grant_type: "password",
      },
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
        "access-control-allow-credentials": true,
        "Access-Control-Allow-Origin": "https://api.statu.space/token",
      },
    });
    alert(response);
  }
}

export default Login;
