import React, {Component} from 'react'
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table'
import '../css/Table.css'
import '../../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css'


const USER_SERVICE_URL = 'StartApi.ashx?ProcessType=Articels&Platform=Android';

function rowClassNameFormat(row, rowIdx) {
    return rowIdx % 2 === 0 ? 'Gold-Row' : 'Silver-Row';
}


class UserTableAutonomous extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isFetching: false,
            users: []
        };
    }

    render() {
        return (

            <table class="table table-hover">
            <thead>
                <tr>
                    <th>No</th>
                    <th>Tarih</th>
                    <th>Bütçe</th>
                    <th>Durum</th>
                </tr>
            </thead>
            <tbody id="listteklif">
                {this.state.users.map(users =>
                    <tr key={users.id} data-id={users.id} data-statu={users.status} class="talep detay ">
                        <td>{users.id}</td><td>{users.createdDate}</td>
                        <td>{users.amount}</td><td>  
                            {(() => {
                                let usersstatus = users.status;
                                switch (usersstatus) {
                                    case 0:
                                        return <div className="btn btn-info">Onay Bekliyor</div>;;
                                        break;
                                    case 1:
                                        return <div className="btn btn-info">Yayında</div>;
                                        break;
                                    case 2:
                                        return <div className="btn btn-warning">Hazırlanıyor</div>;
                                        break;
                                    case 3:
                                        return <div className="btn btn-success">Ödeme Yapınız</div>;
                                        break;
                                    case 5:
                                        return <div className="btn btn-success">Ödeme Onaylandı</div>;
                                        break;
                                    case 6:
                                        return <div className="btn btn-success">Alınacak Hizmet</div>;
                                        break;
                                    case 7:
                                        return <div className="btn btn-success">Alınan Hizmet</div>;
                                        break;
                                    default:
                                        return "Neither";
                                }

                            })()} </td></tr>)}
            </tbody>
        </table>

        )
    }

    componentDidMount() {
        this.fetchUsers();
        this.timer = setInterval(() => this.fetchUsers(), 5000);
    }

    componentWillUnmount() {
        clearInterval(this.timer);
        this.timer = null;
    }

    async fetchUsersAsync() {

        const response = await fetch(USER_SERVICE_URL, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',    
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin":USER_SERVICE_URL,
                "Authorization": "bearer "
            },
        });
        this.setState({ users: await response.json() });


    };

    fetchUsers = this.fetchUsersAsync;
}

export default UserTableAutonomous
