import React, {Component} from 'react'
import '../css/Table.css'
import CallOut from './CallOut';
import Files from './Files';
import WayBillList from './WayBillList';
import TopBar from './TopBar';
import ProductOutModal from './ProductOutModal';
import ProductEditModal from './ProductEditModal';

import LayoutRight from './LayoutRight';


const USER_SERVICE_URL = 'StartApi.ashx?Platform=Android&ProcessType=';

class ArticelsTable extends Component {
    constructor(props) {
        super(props);
        this.state = {

            articels: [],
            orders: [],           
            waybill: [],
            onewaybill: [],
            files:[],

            Piece: 0,
            Dimensions:"",
            OrderId:0,
            Color: "",
            TypeName: "",
            TypeId: 0,

            loopcount: 0,
            totalOutPiece: 0,
            totalOutWeight: 0,      
            articelid:0,    

            iswaybilllist:true,
            isfirstrun:true,
            isShow: true,

            isShowFiles:false,
            ismodalvisible: false,
            isproductoutshow:false,
            isShowOrder: false,
            iscalloutshow: false,
            calloutloading:false,
            isFetching: false,
            topbarvisible:false,
            isLayoutRight:false,
            isproducteditshow:false,

            Articel:"Articel",
            x: 0,
            y: 0,
            articelname:"",
            ActiveArticel:0

        };

        this.GetOrders = this
            .GetOrders
            .bind(this)
            this.productEditShow = this.productEditShow.bind(this)

            this.productOutShow = this.productOutShow.bind(this)
            this.CancelProduct = this.CancelProduct.bind(this)

            this.closeTopBar = this.closeTopBar.bind(this)

            this.LayoutRightShow = this.LayoutRightShow.bind(this)
this.CancelCallOut = this.CancelCallOut.bind(this)


    }

    Closeproductmodal() {

        this.setState({ismodalvisible: false,iscalloutshow: false});

    }
LayoutRightShow(){

    this.setState({isLayoutRight: true,topbarvisible:false,iscalloutshow: false});
    document.getElementById("LayoutRight").style.width = "300px";



}
    productOutShow() {


        this.setState({isproductoutshow: true,iscalloutshow: false});



    }

    productEditShow() {


        this.setState({isproducteditshow: true,iscalloutshow: false});



    }


    CancelProduct(){
        this.setState({isproductoutshow: false,iscalloutshow: false});


    }


    CancelCallOut(){
        this.setState({iscalloutshow: false});
    }

    closeTopBar(){

        this.setState({topbarvisible: false,iscalloutshow: false,ActiveArticel:0});

        document.getElementById("SecondScreen").classList.add("hide");
        document.getElementById("FirstScreen").classList.add("col-md-12");
        document.getElementById("FirstScreen").classList.remove("col-md-4");
        var selectedId ="Articel"+this.state.ActiveArticel
        document.getElementById(selectedId).classList.remove("ActiveArticelRow")

    }

    TransferSummary(weight, piece) {
         this.setState({
            totalOutPiece: 1 + piece,
            totalOutWeight: 1 + weight,
            loopcount: 99
        });

    }

    async GetWayBillPhoto(WayBillId) {}
    async GetWayBillAsync(ArticelId) {
        this.setState({
            waybill:[],isShow: true
        });
        const response = await fetch(USER_SERVICE_URL + "Motion&MotionType=Multi&OrderId=" + ArticelId, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin": USER_SERVICE_URL + "Motion&MotionType=Multi&OrderId=" + ArticelId,
                "Authorization": "bearer "
            }
        });
        this.setState({
            waybill: await response.json(),isShow: false
        });
if(this.state.waybill==''){

    this.setState({iswaybilllist: false});
}else{
    this.setState({iswaybilllist: true});

}
    }

    async GetFilesAsync(ArticelId) {
        this.setState({
            files: [],isShowFiles: false
        });
var url= USER_SERVICE_URL + "Pictures&ArticelId="+ArticelId;
        const response = await fetch(url, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin": url ,
                "Authorization": "bearer "
            }
        });
        this.setState({
            files: await response.json(),isShowFiles: false
        });
if(this.state.files==''){

    this.setState({isShowFiles: false});
}else{
    this.setState({isShowFiles: true});

}
    }









    async GetOrderEdit(orderId, dimensions, color, piece, productTypeName) {

        this.setState({Piece: piece, Dimensions: dimensions, Color: color,OrderId:orderId ,ProductTypeName: productTypeName,ismodalvisible: true,isproducteditshow:true });



    };












    _onMouseMove(e) {
        this.setState({
            x: e.pageX + "px",
            y: e.pageY + "px"
        });

    }
    async GetWaybillforOrder(OrderId, dimensions, color, producttypename, element) {

        this.setState({Dimensions: dimensions,onewaybill:[], Color: color, ProductTypeName: producttypename,calloutloading:true, iscalloutshow: true,isShow: true});

        const response = await fetch(USER_SERVICE_URL + "Motion&MotionType=One&OrderId=" + OrderId, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin": USER_SERVICE_URL + "Motion&MotionType=One&OrderId=" + OrderId,
                "Authorization": "bearer "
            }
        });
        this.setState({
            onewaybill: await response.json(),isShow: false
        });
        var wayPiece = 0;
        var wayWeight = 0;

        this.state.onewaybill.map((w) => wayPiece = wayPiece + parseInt(w.Piece));

        this.setState({loopcount: this.state.onewaybill.length,calloutloading:false, totalOutPiece: wayPiece, totalOutWeight: wayWeight});

    }

    async GetOrders(ArticelId, ArticelName) {



        this.setState({isShow: true,topbarvisible:true,  iscalloutshow: false,articelname:ArticelName});
        {this.GetWayBillAsync(ArticelId)}   
        {this.GetFilesAsync(ArticelId)}   

        this.setState({
            orders: [],

            isShowOrder: true
        });

        if(this.state.ActiveArticel==0){
            this.state.ActiveArticel=ArticelId

        }
        else{

            var selectedId ="Articel"+this.state.ActiveArticel
            document.getElementById(selectedId).classList.remove("ActiveArticelRow")
            this.state.ActiveArticel=ArticelId
        }


        var Clicked ="Articel"+ArticelId
        document.getElementById(Clicked).classList.add("ActiveArticelRow")

        document.getElementById("ArticelName").innerHTML = ArticelName;
        document.getElementById("SecondScreen").classList.remove("hide");

