import React, {Component} from 'react'
import '../css/Table.css'
import CallOut from './CallOut';


const USER_SERVICE_URL = 'StartApi.ashx?Platform=Android&ProcessType=';



class ArticelsTable extends Component {
    constructor(props) {
        super(props);




        this.state = {
            isFetching: false,
            articels: [],
            orders: [],
            isShow: true ,
            ismodalvisible:false,
            isShowOrder:false,
            iscalloutshow:false,
            waybill: [],
            onewaybill:[],
            Piece:0,
            Dimensions:0,
            Color:"",
            TypeName:"",
            TypeId:0,
            loopcount:0,
            totalOutPiece:0,
            totalOutWeight:0,
            popuptop:0,
            popupleft:0,
            x: 0, y: 0


        };


        this.GetOrders = this.GetOrders.bind(this)


    }



    Closeproductmodal(){


        this.setState({ ismodalvisible:false});

    }

    TransferSummary(weight,piece){


        this.setState({ totalOutPiece:1+piece,totalOutWeight:1+weight,loopcount:99});

    }

    async GetWayBillPhoto(WayBillId){
    }


     async GetOrderEdit(OrderId,Dimensions,Color,Piece,ProductTypeName) {

        this.setState({ismodalvisible:true, Piece: Piece,Dimensions:Dimensions,Color:Color,ProductTypeName:ProductTypeName});


        const response = await fetch(USER_SERVICE_URL+"Orders&ArticelId="+OrderId, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',    
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin":USER_SERVICE_URL+"Orders&ArticelId="+OrderId,
                "Authorization": "bearer "
            },
        });


    };

    _onMouseMove(e) {
        this.setState({ x: e.pageX+"px", y: e.pageY-100+"px" });

      }
    async GetWaybillforOrder(OrderId,dimensions,color,producttypename,element){




         this.setState({Dimensions:dimensions,
            Color:color,
            ProductTypeName:producttypename,
            iscalloutshow:true});

        const response = await fetch(USER_SERVICE_URL+"Motion&MotionType=One&OrderId="+OrderId, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',    
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin":USER_SERVICE_URL+"Motion&MotionType=One&OrderId="+OrderId,
                "Authorization": "bearer "
            },
        });
        this.setState({ onewaybill: await response.json()});
         var wayPiece=0;
        var wayWeight=0;

         this.state.onewaybill.map((w) =>
          wayPiece=wayPiece+parseInt(w.Piece) 


      );

      this.setState({ loopcount:this.state.onewaybill.length,totalOutPiece:wayPiece,totalOutWeight:wayWeight});

    }


       async GetOrders(ArticelId,ArticelName) {
        this.setState({ isShow:true,iscalloutshow:false});

        document.getElementById("ArticelName").innerHTML=ArticelName;
        document.getElementById("SecondScreen").classList.add("col-md-8");
        document.getElementById("FirstScreen").classList.add("col-md-4");
        document.getElementById("FirstScreen").classList.remove("col-md-12");


        const response = await fetch(USER_SERVICE_URL+"Orders&ArticelId="+ArticelId, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',    
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin":USER_SERVICE_URL+"Orders&ArticelId="+ArticelId,
                "Authorization": "bearer "
            },
        });
        this.setState({ orders: await response.json(),isShow:false,isShowOrder:true});

    };

    render() {


        return (
            <div className="padd0 col-md-12">

                 <div  className={this.state.isShow ? 'show ProgressSpinnerFlat' : 'opaq0 ProgressSpinnerFlat'} role="progressbar"> 
                <div aria-hidden="true">•</div>
                 <div aria-hidden="true">•</div> 
                 <div aria-hidden="true">•</div> 
                 <div aria-hidden="true">•</div> 
                 <div aria-hidden="true">•</div> 
                 </div>
            <div id="FirstScreen" className="WizardArea padd0 col-md-12">
            <table className="table table-hover">
            <thead>
            <tr className="alert alert-success">
                        <td tabIndex="0" className="tablesorter-header tablesorter-unsorted">Firma</td>
                        <td tabIndex="0" className="tablesorter-header tablesorter-unsorted">Articel / Sipariş</td>
                         <td tabIndex="0" className="tablesorter-header tablesorter-unsorted">#</td>
                    </tr>
            </thead>
            <tbody>
                {this.state.articels.map(a =>
                    <tr key={a.id}   >
                        <td>{a.CustomerName}</td>
                        <td ><span className="ArticelId">AT-{a.id}</span> {a.ArticelName}</td>
                         <td><i onClick={() => this.GetOrders(a.id,a.ArticelName)} className="Icon css-43 SearchIcon "></i></td>
                          </tr>)}
            </tbody>
        </table>
        </div>
        <div id="SecondScreen" className= {this.state.isShowOrder ? 'WizardArea padd0 col-md-8' : 'hide'}>
        <div id="ArticelName" className="ArticelNameHead SSOrder text-capitalize PartHead">
           </div>



             <div id="TestBaby" className="DetailOrders SSOrder  OrderDetailsComment">




   <table className="pointer OrderDetailTable table table-hover">
      <thead>
         <tr className="alert alert-success">
            <td tabIndex="0" className="tablesorter-header tablesorter-unsorted">Adet</td>
            <td tabIndex="0" className="tablesorter-header tablesorter-unsorted">Ölçü</td>
            <td tabIndex="0" className="tablesorter-header tablesorter-unsorted">Renk</td>
            <td tabIndex="0" className="tablesorter-header tablesorter-unsorted">Tip</td>
            <td tabIndex="0" className="tablesorter-header tablesorter-unsorted">#</td>

         </tr>
      </thead>
      <tbody aria-live="polite">
      {this.state.orders.map(o =>
         <tr data-piece={o.Piece} data-product={o.Color} className="MotionData" key={o.id} data-orderid={o.id}>
            <td>
               <div className="ms-CalloutExample">
                  <div className="ms-Callout ms-Callout--arrowLeft ms-Callout--OOBE is-hidden">
                     <div className="ms-Callout-main">
                        <div className="ms-Callout-header">
                           <p className="ms-Callout-title"> </p>
                        </div>
                        <div className="ms-Callout-inner">
                           <div className="ms-Callout-content">
                              <p className="ms-Callout-subText"></p>
                           </div>
                        </div>
                     </div>
                  </div>
                  <div onClick={this._onMouseMove.bind(this)}  className="ms-CalloutExample-button">
                      <button data-piece={o.Piece}  onClick={() => this.GetWaybillforOrder(o.id,o.Dimensions,o.Color,o.ProductTypeName,this)}   
                       className="ms-Button info">{o.Piece} {o.Metrics}</button></div>
                  <br/> 
               </div>
            </td>
            <td>{o.Dimensions} </td>
            <td>{o.Color} </td>

            <td> {o.ProductTypeName} </td>

            <td><i  
             onClick={() => this.GetOrderEdit(o.id,o.Dimensions,o.Color,o.Piece,o.ProductTypeName)}
            title="Düzenle" data-icon-name="Edit" role="presentation" aria-hidden="true" className="ms-Button-icon fleft icon-73"></i></td>
         </tr>
         )}
      </tbody>
   </table>
</div> </div>

<div className={ this.state.iscalloutshow ? '' : 'hide'}>
<div id="PopupWayBill" style={{top: this.state.y ,left:this.state.x} }
  className="ms-ContextualHost is-positioned ms-ContextualHost--arrowLeft is-open ms-ContextualHost--primaryArrow"  >
<CallOut
popupleft={this.state.popuptop}
iscalloutshow={this.state.iscalloutshow}
totalOutPiece={this.state.totalOutPiece}
loopcount={this.state.loopcount}
Dimensions={this.state.Dimensions}
Color={this.state.Color}
ProductTypeName={this.state.ProductTypeName}
onewaybill={this.state.onewaybill}

/>
</div>
</div>


        </div>
        )
    }


    updateDimensions = () => {
        this.setState({ iscalloutshow:false });
      };


    componentDidMount() {
        this.fetcharticels();
        window.addEventListener('resize', this.updateDimensions);




    }

    componentWillUnmount() {
        clearInterval(this.timer);
        this.timer = null;
    }

    async fetcharticelsAsync() {

        const response = await fetch(USER_SERVICE_URL+"Articels", {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',    
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin":USER_SERVICE_URL+"Articels",
                "Authorization": "bearer "
            },
        });
        this.setState({ articels: await response.json(),isShow:false });


    };

    fetcharticels = this.fetcharticelsAsync;
}

export default ArticelsTable
