import React, {Component} from 'react'
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table'
import '../css/Table.css'
import '../../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css'


const USER_SERVICE_URL = 'StartApi.ashx?ProcessType=Articels&Platform=Android';

function rowClassNameFormat(row, rowIdx) {
    return rowIdx % 2 === 0 ? 'Gold-Row' : 'Silver-Row';
}


class UserTableAutonomous extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isFetching: false,
            users: []
        };
    }

    render() {
        return (
            <div>
                <BootstrapTable data={this.state.users} trClassName={rowClassNameFormat}>
                    <TableHeaderColumn isKey dataField='id'></TableHeaderColumn>
                    <TableHeaderColumn dataField='ArticelName'></TableHeaderColumn>
                    <TableHeaderColumn dataField='CustomerName'></TableHeaderColumn>
                    <TableHeaderColumn><i data-articelname="Sadasdasd" data-saletype="1" data-corpid="3224" data-corpname="Akdoku Teks." title="Akdoku Teks. Ve Kimya San. Tic. Ltd. Sti." data-articelid="8305" class="DetailsOrder pointer DetailFind Icon SearchIcon css-43"></i></TableHeaderColumn>
                </BootstrapTable>
                <p>{this.state.isFetching ? '..' : ''}</p>
            </div>
        )
    }

    componentDidMount() {
        this.fetchUsers();
        this.timer = setInterval(() => this.fetchUsers(), 5000);
    }

    componentWillUnmount() {
        clearInterval(this.timer);
        this.timer = null;
    }

    async fetchUsersAsync() {

        const response = await fetch(USER_SERVICE_URL, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',    
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin":USER_SERVICE_URL,
                "Authorization": "bearer "
            },
        });
        this.setState({ users: await response.json() });


    };

    fetchUsers = this.fetchUsersAsync;
}

export default UserTableAutonomous
