import React, {Component} from 'react'
import '../css/Table.css'


const USER_SERVICE_URL = 'StartApi.ashx?ProcessType=Articels&Platform=Android';



class ArticelsTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isFetching: false,
            articels: []
        };
    }

    render() {
        return (

            <table className="table table-hover">
            <thead>
            <tr className="alert alert-success">
                        <td tabindex="0" className="tablesorter-header tablesorter-unsorted">Firma</td>
                        <td tabindex="0" className="tablesorter-header tablesorter-unsorted">Articel / Sipariş</td>
                        <td tabindex="0" className="tablesorter-header tablesorter-unsorted">Tarih</td>
                        <td tabindex="0" className="tablesorter-header tablesorter-unsorted">#</td>
                    </tr>
            </thead>
            <tbody>
                {this.state.articels.map(articels =>
                    <tr key={articels.id} data-id={articels.id}   className="talep detay ">
                        <td>{articels.CustomerName}</td>
                        <td>{articels.ArticelName}</td>
                        <td><i  className="Icon SearchIcon css-43"></i></td> </tr>)}
            </tbody>
        </table>

        )
    }

    componentDidMount() {
        this.fetcharticels();
        this.timer = setInterval(() => this.fetcharticels(), 5000);
    }

    componentWillUnmount() {
        clearInterval(this.timer);
        this.timer = null;
    }

    async fetcharticelsAsync() {

        const response = await fetch(USER_SERVICE_URL, {
            method: "POST",
            cache: "no-cache",
            mode: "cors",
            headers: {
                'Content-Type': 'application/json',    
                "access-control-allow-credentials": false,
                "Access-Control-Allow-Origin":USER_SERVICE_URL,
                "Authorization": "bearer "
            },
        });
        this.setState({ articels: await response.json() });


    };

    fetcharticels = this.fetcharticelsAsync;
}

export default ArticelsTable
